const AuthReducer = (state, action) => {
   switch (action.type) {
      case "Login-Start":
         return ({
           user: null,
           isFetching: true,
           error: false  
         })
      case "Login-Success":
         return ({
            user: action.payload,
            isFetching: false,
            error: false  
         }) 
      case "Login-Failure":
          return ({
            user: null,
            isFetching: false,
            error: action.payload 
          })          
      default:
         return state;     
   } 
}

export default AuthReducer;
