import React, { useContext, useEffect, useState } from 'react';
import "./messenger.css";
import Navbar from "../../react-components/navbar/Navbar.js";
import Conversation from '../../react-components/conversations/Conversation';
import Message from '../../react-components/message/Message';
import OnlineChat from '../../react-components/onlineChat/OnlineChat';
import { AuthContext } from '../../context/AuthContext';
import axios from "axios";

function Messenger() {
    
    const [conversations, setConversations] = useState([]);
    const {user} = useContext(AuthContext);
    
    useEffect(() => {
       const getConversations = async () => {
          try {
            const res = await axios.get("/conversations/" + user._id);
            console.log(res);
          } catch (err) {
             console.log(err); 
          }
       }
        getConversations();
    });

    return (
       <>
        <Navbar/> 
        <div className="messenger">
           <div className="chatMenu">
               <div className="chatMenuWrapper">
                  <input placeholder = "Search for friends" className="chatMenuInput" />
                  <Conversation/>
                  <Conversation/>
                  <Conversation/>
                  <Conversation/>
               </div>
           </div>
           <div className="chatBox">
               <div className="chatBoxWrapper">
                  <div className="chatBoxTop">
                      <Message/>
                      <Message my = {true}/>
                      <Message/>
                      <Message/>
                      <Message/>
                      <Message/>
                      <Message/>
                      <Message/>
                  </div>
                  <div className="chatBoxBottom">
                      <textarea className = "chatMessageInput" placeholder = "write something..."></textarea>
                      <button className="chatSubmitButton">Send</button>
                  </div>
               </div>
           </div>
           <div className="chatOnline">
               <div className="chatOnlineWrapper">
                 <OnlineChat/>
               </div>
           </div>
        </div>
       </>
    )
}

export default Messenger;
