import React, { useContext, useRef } from 'react';
import "./login.css";
import {loginCall} from "../../apiCalls.js";
import { AuthContext } from '../../context/AuthContext';

function Login() {
    
    const email = useRef();
    const password = useRef();
    const {user, isFetching, error, dispatch} = useContext(AuthContext);

    const handleClick = (e) => {
       e.preventDefault(); 
       loginCall({email: email.curent.value, password: password.curent.value}, dispatch);
    };

    console.log(user);

    return (
        <div className="login">
            <div className="loginWrapper">
                <div className="loginLeft">
                   <h3 className="loginLogo">facebook</h3>
                   <span className="loginDescription">
                     Connect with friends and the world around you on facebook.  
                   </span> 
                </div>
                <div className="loginRight" onSubmit ={handleClick}>
                   <form className="loginBox">
                       <input placeholder = "Email" type = "email" required className="loginInput" ref = {email}/>
                       <input placeholder = "Password" type = "password" required className="loginInput" ref = {password} minLength = "6"/>
                       <button className="loginButton">Log In</button>
                       <span className="loginForgot">Forgot Password?</span>
                       <button className="loginRegisterButton"> Sign Up</button>
                   </form> 
                </div>
            </div>
        </div>
    )
}

export default Login
