import {React, useState} from 'react';
import "./post.css";
import {MoreVert} from "@material-ui/icons";
import {Users} from "../../randomData";

function Post({post}) {
    const [like, setLike] = useState(post.like);
    const [isLiked, setIsLiked] = useState(false);

    const likeHandler = () => {
       setLike(isLiked ? like - 1: like + 1); 
    }
    return (
        <div className = "post">
           <div className="postWrapper">
             <div className="postTop">
               <div className="postTopLeft">
                   <img src={Users.filter((u) => u.id === post.userId)[0].profilePicture} alt="" className="postProfileImage" />
                   <span className="postUsername">{Users.filter((u) => u.id === post.userId)[0].username}</span>
                   <span className="postDate">{post.date}</span>
               </div>
               <div className="postTopRight">
                  <MoreVert/>
               </div>  
             </div>
             <div className="postCenter">
                 <span className="postText">{post?.description}</span>
                 <img src={post.photo} alt="" className="postImage" /> 
             </div>
             <div className="postBottom">
                 <div className="postBottomLeft">
                    <img src="/images/like.png" alt="" className="likeIcon"
                      onClick = {likeHandler}
                    /> 
                    <img src="/images/heart.png" alt="" className="likeIcon"
                      onClick = {likeHandler}
                    />
                    <span className="postLikeCounter">{like} people like it</span>
                 </div>
                 <div className="postBottomRight">
                    <span className="postCommentText">{post.comment} comments</span> 
                 </div>
             </div>  
           </div> 
        </div>
    )
}

export default Post
