import {React, useEffect} from 'react';
import "./rightbar.css";
import {Users} from "../../randomData";
import Online from '../online/Online';
import axios from 'axios';

function Rightbar({user}) {
    
    useEffect(() => {
      const getFriends = async () => {
        try {
          const friendList = await axios.get("/users/friends/" + user._id);
        } catch (err) {
           console.log(err);
        }
      };
        getFriends();

    }, [user._id])

    const HomeRightbar = () => {
       return (
          <>
            <div className="birthdayCtr">
                   <img src="/images/gift.png" alt="" className="birthdayImage" />
                   <span className = "birthdayText">
                   <b>lol</b> and <b>3 other friends</b> have a birthday today.
                   </span>
               </div>
               <img src="/images/ad.jpg" alt="" className="rightbarAd" />
               <h4 className="rightbarTitle">Online Friends</h4>
               <ul className="rightbarFriendList">
                   {Users.map(u => (
                     <Online key = {u.id} user = {u} /> 
                   ))}
               </ul>
          </> 
       ) 
    }

    const ProfileRightbar = () => {
        return (
           <>
             <h4 className = "rightbarTitle">User Information</h4>
             <div className="rightbarInfo">
                 <div className="rightbarInfoItem">
                     <span className="rightbarInfoKey">City:</span>
                     <div className="rightbarInfoValue">{user.city}</div>
                 </div>
                 <div className="rightbarInfoItem">
                     <span className="rightbarInfoKey">From:</span>
                     <div className="rightbarInfoValue">{user.GoesTo}</div>
                 </div>
                 <div className="rightbarInfoItem">
                     <span className="rightbarInfoKey">Relationship:</span>
                     <div className="rightbarInfoValue">
                     {user.relationship === 1 ? "Single" : user.relationship === 2 ? "Married" : "-"}
                     </div>
                 </div>
             </div>
             <h4 className = "rightbarTitle">User Friends</h4>
             <div className="rightbarFollowings">
                 <div className="rightbarFollowing">
                    <img src="/images/person/5.jpg" alt="" className="rightbarFollowingImage" /> 
                    <span className="rightbarFollowingName">John Carter</span>
                 </div>
                 
             </div>
           </>
        ) 
     }

    return (
        <div className = "rightbar">
           <div className="rightbarWrapper">
               {user ? <ProfileRightbar/> : <HomeRightbar/>}
           </div> 
        </div>
    )
}

export default Rightbar;
